from nltk.corpus import brown
from nltk import FreqDist
from nltk.classify import DecisionTreeClassifier,accuracy
sfx=FreqDist()
for w in brown.words():
    w=w.lower()
    sfx[w[-1:]]+=1
    sfx[w[-2:]]+=1
    sfx[w[-3:]]+=1
common=sfx.keys()[:100]
'''

print sfx.items()[:5]
[(u'vre', 9), (u'g/l', 10), (u'mny', 1), (u'.32', 1), (u"k's", 109)]
print common[:5]

[u'vre', u'g/l', u'mny', u'.32', u"k's"]
'''

def pos_feats(word):
    fet={}
    for sfx in common:
        fet['endswith %s'%sfx]=word.lower().endswith(sfx)
    return fet
tgd=brown.tagged_words(categories="news")

